import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters: [
        {
          name: 'Frank',
          id: 1
        },
        {
          name: 'Dragon',
          id: 2
        },
        {
          name: 'NightFury',
          id: 3
        }
      ]
    }

  }

  render(){
    return (
      <div className="App">
          <h1>saravana</h1>
          {this.state.monsters.map(monster => (
            <h1 key={monster.id}>{monster.name}</h1>
          ))}
      </div>
    );
  }
}

export default App;
